'use strict';

var core = require('@tauri-apps/api/core');

async function shareFile(path, mime) {
    return await core.invoke('plugin:share|share_file', {
        path,
        mime
    }).then((r) => (r.value ? r.value : null));
}
async function getSharedFiles(path, group) {
    return await core.invoke('plugin:share|get_shared_files', {
        group,
        path
    }).then((r) => (r.files ? r.files : null));
}
async function getSharedFilesPath(path, group) {
    return await core.invoke('plugin:share|get_shared_files_path', {
        group,
        path
    }).then((r) => (r.paths ? r.paths : null));
}

exports.getSharedFiles = getSharedFiles;
exports.getSharedFilesPath = getSharedFilesPath;
exports.shareFile = shareFile;
